mapper.select:
SELECT A.societyId, A.plateNumber, A.plateColor, A.certificateNumber, A.brandNumber, A.carLimitSeat, A.carTonnage, A.carTractionMass, A.carLength, A.carHigh, A.carWidth, A.starTime, A.endTime, A.certificateInstitution, A.policyNumber, A.policyExpireDate, A.gpsStatus, A.carCheckYear, A.carCheckStatus, A.carCheckDate, A.carNextCheckDate, A.updateTime, m1.mvalue certificateType, m2.mvalue carType, m3.mvalue businessScope, m4.mvalue carLevel, m5.mvalue status, m6.mvalue dangerType, c.campanyName FROM CAR_INFORMATION A left join dict_value m1 on A.certificateType =m1.mcode and m1.mtype = "certificateType" LEFT JOIN dict_value m2 on A.carType = m2.mcode and m2.mtype = "carType" LEFT JOIN dict_value m3 on A.businessScope = m3.mcode and m3.mtype ="businessScope" LEFT JOIN dict_value m4 on A.carLevel = m4.mcode and m4.mtype ="techlevel" LEFT JOIN dict_value m5 on A.status = m5.mcode and m5.mtype = "state" LEFT JOIN dict_value m6 on A.dangerType = m6.mcode and m6.mtype ="transportType" left join campany_regist c on A.societyId = c.societyId
SOCIETYID = #{campany.societyId}
AND PLATENUMBER LIKE CONCAT('%',#{car.plateNumber},'%')
AND CARTYPE = #{car.carType}
ORDER BY UPDATETIME
LIMIT #{pagination.startRow},#{pagination.pageSize};

SELECT COUNT(1) FROM CAR_INFORMATION


		SOCIETYID = #{campany.societyId}


		AND PLATENUMBER LIKE CONCAT('%',#{car.plateNumber},'%')


		AND CARTYPE = #{car.carType}
